package gui.icon;

import java.awt.Graphics2D;
import java.awt.Image;
import java.awt.RenderingHints;
import java.awt.image.BufferedImage;
import java.io.ByteArrayInputStream;
import java.io.File;
import java.io.FileInputStream;
import java.io.IOException;
import java.io.InputStream;
import java.util.Base64;

import javax.imageio.ImageIO;
import javax.swing.ImageIcon;

public class Icon {

	//floppy == this program icon
	public static String floppyIco = ""; 
	
	//pcloud
	public static String pcloudIco = "";
	public static String pcloudLogo = "";
	
	//search folder
	public static String searchFolderIco = "";
	
	//local storage
	public static String localStorageIco = "";
	
	public static BufferedImage decode(String str) {
        try {
        	InputStream is = new ByteArrayInputStream(Base64.getDecoder().decode(str));
			return ImageIO.read(is);
		} catch (IOException e) {
			e.printStackTrace();
		}
        return null;
	}
	
	public static BufferedImage decode(String str, int width, int height, boolean bestQuality) {
		return getScaledInstance(new ImageIcon(Base64.getDecoder().decode(str)).getImage(), width, height, bestQuality);
	}
	
	public static BufferedImage getScaledInstance(Image img, int width, int height, boolean bestQuality) {
		BufferedImage bi = new BufferedImage(width, height, BufferedImage.TYPE_INT_ARGB);
        Graphics2D g2d = (Graphics2D)bi.createGraphics();
        if (bestQuality) {
        	g2d.addRenderingHints(new RenderingHints(RenderingHints.KEY_RENDERING, RenderingHints.VALUE_RENDER_QUALITY));
        }
        g2d.drawImage(img, 0, 0, width, height, null);
        g2d.dispose();
        return bi;
	}
	
    public static BufferedImage append(BufferedImage img1, BufferedImage img2) {
        BufferedImage buf = null;
        if(img1 != null && img2 != null) {
            int w1 = img1.getWidth(null);
            int h1 = img1.getHeight(null);
            int w2 = img2.getWidth(null);
            int h2 = img2.getHeight(null);
            int hMax = 0;
            int wMax = 0;
           
            hMax = (h1 >= h2) ? h1 : h2;
            wMax = w1+w2;
            buf = new BufferedImage(wMax, hMax, BufferedImage.TYPE_INT_ARGB);
            Graphics2D g2 = buf.createGraphics();
            g2.drawImage(img1, 0, 0, null);
            g2.drawImage(img2, w1, 0, null);
            g2.dispose();
        }
        return buf;
    }
	
    public static String encode(File file) throws IOException {
    	//just to test if it can read the file as an image
    	ImageIO.read(file);
    	//convert file content to base 64
		FileInputStream fl = new FileInputStream(file);
        byte[] arr = new byte[(int)file.length()];
        fl.read(arr);
        fl.close();
        return Base64.getEncoder().encodeToString(arr);
    }
	
}
